using System;\nusing System.IO;\nusing Aspose.Words;\n\npublic class WordToPdfConverter\n{\n    public static void ConvertToPdf(string inputFilePath, string outputFilePath)\n    {\n        // Load the Word document\n        Document doc = new Document(inputFilePath);\n\n        // Save the document as a PDF\n        doc.Save(outputFilePath, SaveFormat.Pdf);\n    }\n}\n\n// Usage\nstring inputFilePath = Server.MapPath("~/path/to/your/input.docx");\nstring outputFilePath = Server.MapPath("~/path/to/your/output.pdf");\n\nWordToPdfConverter.ConvertToPdf(inputFilePath, outputFilePath);\n\n// Stream the PDF to the browser\nResponse.ContentType = "application/pdf";\nResponse.AppendHeader("Content-Disposition", "attachment; filename=output.pdf");\nResponse.TransmitFile(outputFilePath);\nResponse.End();