using Aspose.Words;\nusing Aspose.Pdf;\nusing Aspose.Pdf.Text;\nusing System.IO;\n\npublic void MergeDocuments(List<string> filePaths, string outputPdfPath)\n{\n    var pdfDocument = new Document();\n    var pdfEditor = new PdfFileEditor();\n\n    foreach (string filePath in filePaths)\n    {\n        if (Path.GetExtension(filePath).Equals(".docx"))\n        {\n            Document wordDoc = new Document(filePath);\n            using (MemoryStream stream = new MemoryStream())\n            {\n                wordDoc.Save(stream, SaveFormat.Pdf);\n                pdfEditor.Append(pdfDocument, new Document(stream));\n            }\n        }\n        else if (Path.GetExtension(filePath).Equals(".pdf"))\n        {\n            pdfEditor.Append(pdfDocument, new Document(filePath));\n        }\n        // Add similar blocks for other file types like images, text, etc.\n    }\n\n    pdfDocument.Save(outputPdfPath);\n}