protected void Page_Load(object sender, EventArgs e)\n{\n    if (!Page.IsClientScriptBlockRegistered("feeAmountCheckScript"))\n    {\n        string script = @"\n            <script type='text/javascript'>\n                function feeAmountCheck(source, arguments)\n                {\n                    var amountDue = document.getElementById('ctl00_footerContentHolder_Fees1_FeeDue');\n                    var amountPaid = document.getElementById('ctl00_footerContentHolder_Fees1_FeePaid');\n\n                    if (amountDue && amountPaid && amountDue.value > 0 && parseFloat(amountDue.value) >= parseFloat(amountPaid.value))\n                    {\n                        arguments.IsValid = true;\n                    }\n                    else\n                    {\n                        arguments.IsValid = false;\n                    }\n                }\n            </script>";\n\n        Page.ClientScript.RegisterClientScriptBlock(this.GetType(), "feeAmountCheckScript", script, false);\n    }\n}