using System;\nusing System.Data.SqlClient;\nusing System.Web;\nusing System.Web.SessionState;\nusing System.IO;\nusing System.Runtime.Serialization.Formatters.Binary;\n\npublic class SessionStateReader\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string sessionId = "YourSessionID";\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            conn.Open();\n            string query = "SELECT SessionItemShort, SessionItemLong FROM ASPStateTempSessions WHERE SessionID = @SessionID";\n            SqlCommand cmd = new SqlCommand(query, conn);\n            cmd.Parameters.AddWithValue("@SessionID", sessionId);\n\n            using (SqlDataReader reader = cmd.ExecuteReader())\n            {\n                if (reader.Read())\n                {\n                    byte[] sessionData = reader["SessionItemShort"] as byte[] ?? reader["SessionItemLong"] as byte[];\n\n                    if (sessionData != null)\n                    {\n                        DeserializeSessionItems(sessionData);\n                    }\n                }\n            }\n        }\n    }\n\n    private static void DeserializeSessionItems(byte[] sessionData)\n    {\n        using (MemoryStream ms = new MemoryStream(sessionData))\n        {\n            BinaryFormatter formatter = new BinaryFormatter();\n            SessionStateItemCollection sessionItems = (SessionStateItemCollection)formatter.Deserialize(ms);\n\n            foreach (string key in sessionItems.Keys)\n            {\n                Console.WriteLine($"Key: {key}, Value: {sessionItems[key]}");\n            }\n        }\n    }\n}