public class ClassA\n{\n    public string FieldA { get; set; }\n    public string FieldB { get; set; }\n    public ClassB FieldC { get; set; }\n\n    // Constructor\n    public ClassA(string fieldA = "DefaultA", string fieldB = "DefaultB", ClassB fieldC = null)\n    {\n        FieldA = fieldA;\n        FieldB = fieldB;\n        FieldC = fieldC ?? new ClassB();\n    }\n}\n\npublic class ClassB\n{\n    public string FieldD { get; set; }\n\n    // Constructor\n    public ClassB(string fieldD = "DefaultD")\n    {\n        FieldD = fieldD;\n    }\n}