using System;\nusing System.Net;\n\npublic class BindableWebClient : WebClient\n{\n    private readonly IPAddress _localIpAddress;\n\n    public BindableWebClient(IPAddress localIpAddress)\n    {\n        _localIpAddress = localIpAddress;\n    }\n\n    protected override WebRequest GetWebRequest(Uri address)\n    {\n        var request = base.GetWebRequest(address) as HttpWebRequest;\n        if (request != null)\n        {\n            request.ServicePoint.BindIPEndPointDelegate =\n                (servicePoint, remoteEndPoint, retryCount) => new IPEndPoint(_localIpAddress, 0);\n        }\n        return request;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        using (var client = new BindableWebClient(IPAddress.Parse("10.10.10.1")))\n        {\n            var result = client.DownloadString("http://10.10.10.15");\n            Console.WriteLine(result);\n        }\n    }\n}