using System;\nusing System.Collections.Generic;\nusing System.Web.UI.WebControls;\n\npublic void GetUniqueItems(DropDownList dropdownList)\n{\n    HashSet<string> uniqueItems = new HashSet<string>();\n\n    foreach (ListItem item in dropdownList.Items)\n    {\n        uniqueItems.Add(item.Text);\n    }\n\n    // Clear the DropDownList and add unique items back\n    dropdownList.Items.Clear();\n    foreach (string uniqueItem in uniqueItems)\n    {\n        dropdownList.Items.Add(new ListItem(uniqueItem));\n    }\n}