[TestMethod]\n   public void TestStringLengthValidation()\n   {\n       // Arrange\n       var user = new User { Name = new string('A', 101), Email = "test@example.com" }; // Assuming max length is 100\n       \n       // Act\n       var result = user.Validate();\n       \n       // Assert\n       Assert.IsFalse(result.IsValid);\n       Assert.AreEqual("Name cannot be longer than 100 characters.", result.Errors["Name"]);\n   }\n\n   [TestMethod]\n   public void TestDateRangeValidation()\n   {\n       // Arrange\n       var event = new Event { StartDate = DateTime.Now.AddDays(1), EndDate = DateTime.Now };\n       \n       // Act\n       var result = event.Validate();\n       \n       // Assert\n       Assert.IsFalse(result.IsValid);\n       Assert.AreEqual("End date must be after start date.", result.Errors["EndDate"]);\n   }