public class MyClass\n{\n    private NonThreadSafeObject nonThreadSafeObject;\n\n    public MyClass()\n    {\n        nonThreadSafeObject = new NonThreadSafeObject();\n    }\n\n    public void DoWork()\n    {\n        // Queuing a thread that does not interact with nonThreadSafeObject\n        ThreadPool.QueueUserWorkItem(WorkMethod);\n    }\n\n    private void WorkMethod(Object stateInfo)\n    {\n        // This method does not reference nonThreadSafeObject\n    }\n\n    public void AccessNonThreadSafeObject()\n    {\n        lock (nonThreadSafeObject)\n        {\n            // Interact with nonThreadSafeObject safely\n        }\n    }\n}