public abstract class FooBase\n{\n    public event Action<string> StatusTextChanged;\n\n    private string _statusText = "Idle.";\n    public string StatusText\n    {\n        get => _statusText;\n        protected set\n        {\n            if (_statusText != value)\n            {\n                _statusText = value;\n                StatusTextChanged?.Invoke(value);\n            }\n        }\n    }\n}