using System;\n\npublic class Employee\n{\n    public int ID { get; set; }\n    public string Name { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        Employee[] employees = new Employee[]\n        {\n            new Employee { ID = 5, Name = "Frank Grimes" },\n            new Employee { ID = 6, Name = "Tim O'Reilly" }\n        };\n\n        string[] sqlStatements = GenerateSqlStatements(employees);\n\n        foreach (string sql in sqlStatements)\n        {\n            Console.WriteLine(sql);\n        }\n    }\n\n    private static string[] GenerateSqlStatements(Employee[] employees)\n    {\n        string[] sqlStatements = new string[employees.Length];\n\n        for (int i = 0; i < employees.Length; i++)\n        {\n            string escapedName = EscapeSqlString(employees[i].Name);\n            sqlStatements[i] = string.Format(\n                "INSERT INTO Employees (id, name) VALUES ({0}, '{1}')",\n                employees[i].ID,\n                escapedName\n            );\n        }\n\n        return sqlStatements;\n    }\n\n    private static string EscapeSqlString(string value)\n    {\n        if (value == null) return null;\n\n        return value.Replace("'", "''");\n    }\n}