public void SetValidationGroup(string validationGroup)\n{\n    foreach (Control ctrl in this.Controls)\n    {\n        if (ctrl is BaseValidator)\n        {\n            ((BaseValidator)ctrl).ValidationGroup = validationGroup;\n        }\n        else if (ctrl.HasControls())\n        {\n            SetValidationGroupRecursive(ctrl, validationGroup);\n        }\n    }\n    \n    ValidationSummary validationSummary = this.FindControl("ValidationSummary1") as ValidationSummary;\n    if (validationSummary != null)\n    {\n        validationSummary.ValidationGroup = validationGroup;\n    }\n}\n\nprivate void SetValidationGroupRecursive(Control parent, string validationGroup)\n{\n    foreach (Control ctrl in parent.Controls)\n    {\n        if (ctrl is BaseValidator)\n        {\n            ((BaseValidator)ctrl).ValidationGroup = validationGroup;\n        }\n        else if (ctrl.HasControls())\n        {\n            SetValidationGroupRecursive(ctrl, validationGroup);\n        }\n    }\n}