public class Person\n{\n    private int _personId;\n    \n    // Fields to hold the instances of the variable classes\n    private ClassA _classA;\n    private ClassB _classB;\n    \n    public Person(int personId)\n    {\n        _personId = personId;\n    }\n    \n    // Property for ClassA with lazy instantiation\n    public ClassA ClassA\n    {\n        get\n        {\n            if (_classA == null)\n            {\n                _classA = new ClassA(_personId);\n            }\n            return _classA;\n        }\n    }\n    \n    // Property for ClassB with lazy instantiation\n    public ClassB ClassB\n    {\n        get\n        {\n            if (_classB == null)\n            {\n                _classB = new ClassB(_personId);\n            }\n            return _classB;\n        }\n    }\n}\n\npublic class ClassA\n{\n    private int _personId;\n    \n    public ClassA(int personId)\n    {\n        _personId = personId;\n        // Retrieve necessary data using _personId\n    }\n}\n\npublic class ClassB\n{\n    private int _personId;\n    \n    public ClassB(int personId)\n    {\n        _personId = personId;\n        // Retrieve necessary data using _personId\n    }\n}