using iText.Kernel.Pdf;\nusing iText.Kernel.Utils;\nusing System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Web;\nusing System.Web.UI;\n\npublic partial class PrintPDF : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Example list of PDF file paths selected by the user\n        List<string> selectedPdfFiles = new List<string>\n        {\n            Server.MapPath("~/pdfs/sample1.pdf"),\n            Server.MapPath("~/pdfs/sample2.pdf")\n        };\n\n        // Merge the selected PDFs\n        byte[] mergedPdf = MergePDFs(selectedPdfFiles);\n\n        // Send the merged PDF to the client\n        Response.ContentType = "application/pdf";\n        Response.AddHeader("Content-Disposition", "inline; filename=merged.pdf");\n        Response.OutputStream.Write(mergedPdf, 0, mergedPdf.Length);\n        Response.End();\n    }\n\n    private byte[] MergePDFs(List<string> fileNames)\n    {\n        using (MemoryStream ms = new MemoryStream())\n        {\n            PdfDocument pdfDoc = new PdfDocument(new PdfWriter(ms));\n            PdfMerger merger = new PdfMerger(pdfDoc);\n\n            foreach (string file in fileNames)\n            {\n                PdfDocument sourcePdf = new PdfDocument(new PdfReader(file));\n                merger.Merge(sourcePdf, 1, sourcePdf.GetNumberOfPages());\n                sourcePdf.Close();\n            }\n\n            pdfDoc.Close();\n            return ms.ToArray();\n        }\n    }\n}