using System;\n   using System.Data.OleDb;\n\n   class Program\n   {\n       static void Main()\n       {\n           string fooDbConnectionString = @"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=path\to\fooDb.mdb;";\n           string barDbConnectionString = @"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=path\to\barDb.mdb;";\n\n           using (OleDbConnection fooConnection = new OleDbConnection(fooDbConnectionString))\n           using (OleDbConnection barConnection = new OleDbConnection(barDbConnectionString))\n           {\n               fooConnection.Open();\n               barConnection.Open();\n\n               string[] tables = { "Table1", "Table2", "Table3", "Table4" };\n\n               foreach (var table in tables)\n               {\n                   string deleteQuery = $"DELETE FROM {table}";\n                   string insertQuery = $"INSERT INTO {table} SELECT * FROM [{table}] IN '{barConnection.DataSource}'";\n\n                   using (OleDbCommand deleteCommand = new OleDbCommand(deleteQuery, fooConnection))\n                   {\n                       deleteCommand.ExecuteNonQuery();\n                   }\n                   using (OleDbCommand insertCommand = new OleDbCommand(insertQuery, fooConnection))\n                   {\n                       insertCommand.ExecuteNonQuery();\n                   }\n               }\n           }\n       }\n   }