using System;\n   using Microsoft.Office.Interop.Access.Dao;\n\n   class Program\n   {\n       static void Main()\n       {\n           string fooDbPath = @"path\to\fooDb.mdb";\n           string newBarDbPath = @"path\to\newBarDb.mdb";\n           \n           DBEngine dbe = new DBEngine();\n           Database db = dbe.OpenDatabase(fooDbPath);\n\n           string[] tables = { "Table1", "Table2", "Table3", "Table4" };\n\n           foreach (var tableName in tables)\n           {\n               TableDef tableDef = db.TableDefs[tableName];\n               tableDef.Connect = $"MS Access;PWD=;DATABASE={newBarDbPath}";\n               tableDef.RefreshLink();\n           }\n\n           db.Close();\n       }\n   }