using System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "1;2;;3;";\n        char[] delimiter = new char[] { ';' };\n        string[] array = input.Split(delimiter);\n        \n        // Use LINQ to filter out null or empty values\n        string[] filteredArray = array.Where(s => !string.IsNullOrEmpty(s)).ToArray();\n        \n        // Output the filtered array\n        foreach (string value in filteredArray)\n        {\n            Console.WriteLine(value);\n        }\n    }\n}