using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load the XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load("path_to_your_file.xml");\n\n        // Find the nodes using XPath\n        XmlNodeList nodes = xmlDoc.SelectNodes("//YourXPathExpression");\n\n        // Iterate through the nodes and modify the InnerXml property\n        foreach (XmlNode node in nodes)\n        {\n            node.InnerXml = "New Inner XML Content";\n        }\n\n        // Save the changes back to the file\n        xmlDoc.Save("path_to_your_file.xml");\n    }\n}