using System;\nusing System.Globalization;\nusing System.Windows.Controls;\n\npublic class ExceptionValidationRule : ValidationRule\n{\n    public override ValidationResult Validate(object value, CultureInfo cultureInfo)\n    {\n        try\n        {\n            // Your validation logic here\n            // For example, if the value is invalid, throw an exception\n            throw new InvalidOperationException("Your custom error message here.");\n        }\n        catch (Exception ex)\n        {\n            // Unwrap inner exception to get the actual message\n            while (ex.InnerException != null)\n            {\n                ex = ex.InnerException;\n            }\n            return new ValidationResult(false, ex.Message);\n        }\n    }\n}