using System;\nusing System.Net.Mail;\nusing System.Text.RegularExpressions;\n\npublic class MailProcessor\n{\n    public void ProcessMail(MailMessage message)\n    {\n        string body = message.Body;\n        string subject = message.Subject;\n\n        // Example: Extracting data using regex\n        Match match = Regex.Match(body, "Your regex here");\n        if (match.Success)\n        {\n            subject = match.Groups[1].Value; // Adjust based on your regex\n            message.Subject = subject;\n\n            // Forward email\n            SmtpClient client = new SmtpClient("smtp.example.com");\n            MailMessage forwardMessage = new MailMessage("from@example.com", "to@example.com", subject, body);\n            client.Send(forwardMessage);\n        }\n    }\n}