// Assuming dataGridView is your DataGridView control\n// and yourObjectList is the list bound to the DataGridView\n\n// Add a new blank object to the list\nyourObjectList.Add(new MyObject());\n\n// Reset the DataGridView DataSource\ndataGridView.DataSource = null;\ndataGridView.DataSource = yourObjectList;\n\n// Get the index of the new row\nint newRow = dataGridView.Rows.Count - 1;\n\n// Set the focus to the second cell in the new row\ndataGridView.CurrentCell = dataGridView.Rows[newRow].Cells[1];\ndataGridView.BeginEdit(true);