using System;\nusing System.Collections.Generic;\nusing System.Drawing; // Make sure to include System.Drawing for Rectangle\n\nclass Program\n{\n    static void Main()\n    {\n        List<Rectangle> rects = new List<Rectangle>();\n\n        for (int i = 0; i < 5; i++)\n        {\n            rects.Add(new Rectangle(1, 1, 1, 1));\n        }\n\n        foreach (Rectangle item in rects)\n        {\n            Console.WriteLine(item);\n        }\n\n        // Correct way to modify the Rectangle in the list\n        Rectangle r = rects[1];\n        r.Inflate(100, 100);\n        rects[1] = r;\n\n        foreach (Rectangle item in rects)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}