using System;\nusing System.Collections.Generic;\nusing System.Windows.Forms;\n\npublic class AccessDataTypes\n{\n    public static void Main()\n    {\n        List<string> dataTypes = new List<string>\n        {\n            "Text",\n            "Memo",\n            "Byte",\n            "Integer",\n            "Long Integer",\n            "Single",\n            "Double",\n            "Currency",\n            "AutoNumber",\n            "Date/Time",\n            "Yes/No",\n            "OLE Object",\n            "Hyperlink",\n            "Lookup Wizard"\n        };\n\n        ComboBox comboBox = new ComboBox();\n        comboBox.DataSource = dataTypes;\n        \n        // Assuming you have a Form to add this ComboBox to\n        Form form = new Form();\n        form.Controls.Add(comboBox);\n        Application.Run(form);\n    }\n}