using Aspose.Imaging;\nusing Aspose.Imaging.ImageOptions;\nusing System;\nusing System.IO;\n\npublic class ThumbnailGenerator\n{\n    public static void GenerateThumbnail(string inputFilePath, string outputFilePath, int thumbnailWidth, int thumbnailHeight)\n    {\n        // Load the EPS or AI file\n        using (Image image = Image.Load(inputFilePath))\n        {\n            // Calculate the new dimensions while maintaining aspect ratio\n            double aspectRatio = (double)image.Width / image.Height;\n            int newWidth, newHeight;\n\n            if (aspectRatio > 1)\n            {\n                newWidth = thumbnailWidth;\n                newHeight = (int)(thumbnailWidth / aspectRatio);\n            }\n            else\n            {\n                newWidth = (int)(thumbnailHeight * aspectRatio);\n                newHeight = thumbnailHeight;\n            }\n\n            // Create and save the thumbnail\n            using (var thumbnail = image.Resize(newWidth, newHeight, ResizeType.LanczosResample))\n            {\n                thumbnail.Save(outputFilePath, new JpegOptions()); // You can use other formats like PngOptions, etc.\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string inputFilePath = "path/to/your/input/file.eps"; // or .ai\n        string outputFilePath = "path/to/your/output/thumbnail.jpg";\n\n        GenerateThumbnail(inputFilePath, outputFilePath, 150, 150);\n\n        Console.WriteLine("Thumbnail generated successfully.");\n    }\n}