using System;\nusing System.Management;\n\nclass SmartData\n{\n    static void Main()\n    {\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM Win32_DiskDrive");\n\n        foreach (ManagementObject disk in searcher.Get())\n        {\n            Console.WriteLine("Disk: {0}", disk["Model"]);\n            ManagementObjectSearcher smartDataSearcher = new ManagementObjectSearcher(\n                "SELECT * FROM MSStorageDriver_FailurePredictStatus WHERE InstanceName LIKE '%" + disk["DeviceID"].ToString().Replace("\\", "\\\\") + "%'");\n\n            foreach (ManagementObject smartData in smartDataSearcher.Get())\n            {\n                Console.WriteLine("PredictFailure: {0}", smartData["PredictFailure"]);\n            }\n        }\n    }\n}