using System;\n\npublic class Post\n{\n    public int Votes { get; set; }\n    public DateTime CreatedAt { get; set; }\n\n    public double GetScore()\n    {\n        // Calculate the age of the post in hours\n        double ageInHours = (DateTime.UtcNow - CreatedAt).TotalHours;\n\n        // Calculate the score using the Hacker News ranking formula\n        return (Votes - 1) / Math.Pow(ageInHours + 2, 1.8);\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        Post post = new Post\n        {\n            Votes = 100,\n            CreatedAt = DateTime.UtcNow.AddHours(-10)\n        };\n\n        Console.WriteLine("Post score: " + post.GetScore());\n    }\n}