for (int i = 1; i <= 30; i++)\n{\n    string propertyName = "Text" + i.ToString();\n    try\n    {\n        interopObject.GetType().InvokeMember(\n            propertyName,\n            System.Reflection.BindingFlags.SetProperty,\n            null,\n            interopObject,\n            new object[] { someValue });\n    }\n    catch (Exception ex)\n    {\n        // Handle any exceptions that occur here\n        Console.WriteLine($"Failed to set property {propertyName}: {ex.Message}");\n    }\n}