using System;\nusing System.Diagnostics;\nusing System.Linq;\nusing System.Reflection;\nusing System.Windows.Forms;\n\npublic class AboutBox : Form\n{\n    public AboutBox()\n    {\n        InitializeComponent();\n        LoadAssemblies();\n    }\n\n    private void LoadAssemblies()\n    {\n        // Get managed assemblies\n        var managedAssemblies = AppDomain.CurrentDomain.GetAssemblies()\n            .Select(a => new { Name = a.FullName, Location = a.Location });\n\n        // Get unmanaged assemblies\n        var process = Process.GetCurrentProcess();\n        var unmanagedAssemblies = process.Modules.Cast<ProcessModule>()\n            .Select(m => new { Name = m.ModuleName, Location = m.FileName });\n\n        // Combine both lists\n        var allAssemblies = managedAssemblies.Concat(unmanagedAssemblies);\n\n        // Display the assemblies (you can add this to a UI element like a ListBox or DataGridView)\n        foreach (var assembly in allAssemblies)\n        {\n            Console.WriteLine($"Name: {assembly.Name}, Location: {assembly.Location}");\n        }\n    }\n\n    private void InitializeComponent()\n    {\n        this.Text = "About Box";\n        this.Size = new System.Drawing.Size(400, 300);\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new AboutBox());\n    }\n}