public class SyncService\n{\n    private readonly HttpClient _httpClient;\n    private readonly string _webApiUrl;\n\n    public SyncService(string webApiUrl)\n    {\n        _httpClient = new HttpClient();\n        _webApiUrl = webApiUrl;\n    }\n\n    public async Task SyncProduct(Product product)\n    {\n        var response = await _httpClient.PostAsJsonAsync($"{_webApiUrl}/api/product/addProduct", product);\n        response.EnsureSuccessStatusCode();\n    }\n\n    public async Task DeleteProduct(int productId)\n    {\n        var response = await _httpClient.DeleteAsync($"{_webApiUrl}/api/product/deleteProduct/{productId}");\n        response.EnsureSuccessStatusCode();\n    }\n    \n    // Additional methods for other sync operations\n}