public class SimplePersonModelBinder : IModelBinder\n{\n    public object BindModel(ControllerContext controllerContext, ModelBindingContext bindingContext)\n    {\n        var request = controllerContext.HttpContext.Request;\n\n        var firstName = request.Form.Get("FirstName");\n        var lastName = request.Form.Get("LastName");\n        var petName = request.Form.Get("PetName");\n\n        return new SimplePerson\n        {\n            FirstName = firstName,\n            LastName = lastName,\n            PetName = petName\n        };\n    }\n}\n\n// Register the custom model binder\nModelBinders.Binders.Add(typeof(SimplePerson), new SimplePersonModelBinder());