protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Example flag from database\n        bool needValidation = GetValidationFlagFromDatabase();\n\n        // Dynamically created TextBox\n        TextBox dynamicTextBox = new TextBox();\n        dynamicTextBox.ID = "DynamicTextBox";\n        form1.Controls.Add(dynamicTextBox);\n\n        if (needValidation)\n        {\n            // Add validation control\n            RequiredFieldValidator validator = new RequiredFieldValidator();\n            validator.ControlToValidate = dynamicTextBox.ID;\n            validator.ErrorMessage = "This field is required.";\n            validator.ForeColor = System.Drawing.Color.Red;\n            form1.Controls.Add(validator);\n        }\n    }\n}\n\nprivate bool GetValidationFlagFromDatabase()\n{\n    // Simulate reading flag from database\n    return true;\n}