using System;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class CertificateInfo\n{\n    public static void Main()\n    {\n        // Open the Local Machine Certificate Store\n        X509Store store = new X509Store(StoreName.My, StoreLocation.LocalMachine);\n        store.Open(OpenFlags.ReadOnly);\n\n        // Retrieve the certificates from the store\n        X509Certificate2Collection certificates = store.Certificates;\n\n        // Iterate through the certificates and display information\n        foreach (X509Certificate2 cert in certificates)\n        {\n            Console.WriteLine("Issued To: " + cert.Subject);\n            Console.WriteLine("Issued By: " + cert.Issuer);\n            Console.WriteLine("Expiration Date: " + cert.NotAfter);\n            Console.WriteLine("Thumbprint: " + cert.Thumbprint);\n            Console.WriteLine();\n        }\n\n        // Close the store\n        store.Close();\n    }\n}