using (var context = new YourDbContext())\n{\n    var timesheetId = /* your timesheet ID */;\n    var beginDate = DateTime.ParseExact(/* your begin date */, "dd/MM/yyyy", CultureInfo.InvariantCulture);\n    var endDate = DateTime.ParseExact(/* your end date */, "dd/MM/yyyy", CultureInfo.InvariantCulture);\n\n    var query = context.Tasks\n        .Where(t => t.TimeEntries.Any(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate))\n        .Select(t => new\n        {\n            TaskName = t.TaskName,\n            Sunday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Sunday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Monday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Monday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Tuesday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Tuesday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Wednesday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Wednesday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Thursday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Thursday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Friday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Friday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0),\n            Saturday = t.TimeEntries.Where(te => te.TimesheetID == timesheetId && te.StartTime >= beginDate && te.StartTime <= endDate && te.StartTime.DayOfWeek == DayOfWeek.Saturday)\n                                  .Sum(te => (int?)DbFunctions.DiffMinutes(te.StartTime, te.EndTime) ?? 0)\n        })\n        .ToList();\n}