using System;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class CertificateExtensionsChecker\n{\n    public static void Main()\n    {\n        // Open the certificate store\n        X509Store store = new X509Store("My", StoreLocation.LocalMachine);\n        store.Open(OpenFlags.OpenExistingOnly | OpenFlags.ReadOnly);\n\n        // Find your certificate (replace 'YourCertName' with the actual subject name)\n        X509Certificate2Collection certs = store.Certificates.Find(X509FindType.FindBySubjectName, "YourCertName", false);\n\n        if (certs.Count > 0)\n        {\n            X509Certificate2 cert = certs[0];\n            Console.WriteLine("Certificate found: " + cert.Subject);\n\n            foreach (var extension in cert.Extensions)\n            {\n                Console.WriteLine("Extension OID: " + extension.Oid.Value + " (" + extension.Oid.FriendlyName + ")");\n                Console.WriteLine("Raw Data: " + BitConverter.ToString(extension.RawData));\n            }\n\n            if (cert.Extensions.Count == 0)\n            {\n                Console.WriteLine("No extensions found.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Certificate not found.");\n        }\n\n        // Close the store\n        store.Close();\n    }\n}