using System;\nusing System.Linq;\n\npublic class Hit\n{\n    public string Page { get; set; }\n    public DateTime Date { get; set; }\n}\n\npublic class YourDbContext : DbContext\n{\n    public DbSet<Hit> Hits { get; set; }\n}\n\npublic void GetTopPages(YourDbContext context)\n{\n    DateTime startDate = new DateTime(2009, 1, 14);\n    DateTime endDate = new DateTime(2009, 1, 15);\n\n    var result = context.Hits\n        .Where(hit => hit.Date >= startDate && hit.Date < endDate)\n        .GroupBy(hit => hit.Page)\n        .Select(group => new\n        {\n            Page = group.Key,\n            Number = group.Count()\n        })\n        .OrderByDescending(x => x.Number)\n        .Take(10)\n        .ToList();\n\n    foreach (var item in result)\n    {\n        Console.WriteLine($"Page: {item.Page}, Number: {item.Number}");\n    }\n}