protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Example: Add items to the CheckBoxList\n        CheckBoxList1.Items.Add(new ListItem("Item 1", "1"));\n        CheckBoxList1.Items.Add(new ListItem("Item 2", "2"));\n\n        // Apply styles to each item\n        foreach (ListItem item in CheckBoxList1.Items)\n        {\n            if (item.Value == "1")\n            {\n                item.Attributes.CssStyle.Add("color", "green");\n                item.Attributes.CssStyle.Add("font-style", "italic");\n            }\n            else\n            {\n                item.Attributes.CssStyle.Add("color", "orange");\n            }\n        }\n    }\n}