using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        PerformanceCounter gen2Collections = new PerformanceCounter(".NET CLR Memory", "# Gen 2 Collections", "_Global_");\n\n        while (true)\n        {\n            Console.WriteLine($"Gen 2 Collections: {gen2Collections.NextValue()}");\n            System.Threading.Thread.Sleep(1000);\n        }\n    }\n}