using System;\nusing System.Web;\n\npublic class HttpsRedirectModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication application = (HttpApplication)sender;\n        HttpContext context = application.Context;\n\n        if (context.Request.IsSecureConnection == false && ShouldRedirectToHttps(context.Request))\n        {\n            string httpsUrl = "https://" + context.Request.Url.Host + context.Request.Url.PathAndQuery;\n            context.Response.Redirect(httpsUrl);\n        }\n    }\n\n    private bool ShouldRedirectToHttps(HttpRequest request)\n    {\n        // Add your custom logic here to decide which pages should be redirected to HTTPS.\n        // For example:\n        return request.Url.AbsolutePath.StartsWith("/secure");\n    }\n\n    public void Dispose() { }\n}