using (var context = new YourDataContext())\n{\n    var query = from o in context.Orders\n                join od in \n                    (from od in context.OrdersDetail\n                     where od.Status == "A"\n                     select od)\n                on o.Id equals od.OrderId into orderDetails\n                from od in orderDetails.DefaultIfEmpty()\n                select new\n                {\n                    Order = o,\n                    OrderDetail = od\n                };\n\n    var result = query.ToList();\n}