using (var context = new YourDbContext())\n{\n    int productId = 1; // The ID of the product you want to update\n    int customerId = 123; // The CustomerId you need to include\n    string newName = "Updated Product Name"; // The new name for the product\n\n    var product = context.Products.SingleOrDefault(p => p.Id == productId && p.CustomerId == customerId);\n    \n    if (product != null)\n    {\n        product.Name = newName;\n        context.SaveChanges();\n    }\n    else\n    {\n        // Handle the case where the product was not found\n        throw new Exception("Product not found or does not belong to the specified customer.");\n    }\n}