DataTable dataTable = new DataTable();\ndataTable.Columns.Add("Column1", typeof(string));\n\n// Add some data to the DataTable\ndataTable.Rows.Add("apple");\ndataTable.Rows.Add("Banana");\ndataTable.Rows.Add("cherry");\n\n// Create a DataView from the DataTable\nDataView dataView = dataTable.DefaultView;\n\n// Set the Sort property to the column name you want to sort by\ndataView.Sort = "Column1";\n\n// Alternatively, you can create a new DataView with sorting\nDataView sortedView = new DataView(dataTable)\n{\n    Sort = "Column1"\n};\n\n// To display the sorted data\nforeach (DataRowView rowView in sortedView)\n{\n    Console.WriteLine(rowView["Column1"]);\n}