using System;\nusing System.IO;\nusing System.Linq;\n\npublic class FileFilter\n{\n    public static void Main(string[] args)\n    {\n        string jobDir = @"C:\Your\Directory\Path";\n        string[] excludedExtensions = { ".exe", ".tmp", ".log" }; // Add the extensions you want to exclude\n\n        var files = Directory.GetFiles(jobDir)\n            .Where(file => !excludedExtensions.Contains(Path.GetExtension(file)))\n            .ToArray();\n\n        foreach (var file in files)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}