using System;\nusing System.Net;\nusing System.Security.Cryptography.X509Certificates;\nusing System.Net.Http;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the certificate from file with the private key (in a PFX or P12 file)\n        var certificate = new X509Certificate2(@"C:\path\to\your\certificate.pfx", "your_password");\n\n        // Create an HttpClientHandler and set the client certificate\n        var handler = new HttpClientHandler();\n        handler.ClientCertificates.Add(certificate);\n\n        // Create an HttpClient with the handler\n        var httpClient = new HttpClient(handler);\n\n        // Make your request\n        var response = httpClient.GetAsync("https://your-secure-api-endpoint").Result;\n        var content = response.Content.ReadAsStringAsync().Result;\n\n        Console.WriteLine(content);\n    }\n}