using System;\nusing System.Drawing;\n\npublic class AverageColorCalculator\n{\n    public static void Main(string[] args)\n    {\n        // Load the bitmap\n        Bitmap bitmap = new Bitmap("path_to_your_image");\n\n        // Variables to hold the sum of RGB values\n        long sumR = 0, sumG = 0, sumB = 0;\n        int pixelCount = bitmap.Width * bitmap.Height;\n\n        // Iterate through each pixel in the bitmap\n        for (int y = 0; y < bitmap.Height; y++)\n        {\n            for (int x = 0; x < bitmap.Width; x++)\n            {\n                Color pixelColor = bitmap.GetPixel(x, y);\n                sumR += pixelColor.R;\n                sumG += pixelColor.G;\n                sumB += pixelColor.B;\n            }\n        }\n\n        // Calculate the average values\n        int avgR = (int)(sumR / pixelCount);\n        int avgG = (int)(sumG / pixelCount);\n        int avgB = (int)(sumB / pixelCount);\n\n        // Print the average RGB values\n        Console.WriteLine($"Average R: {avgR}, Average G: {avgG}, Average B: {avgB}");\n    }\n}