using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        List<long> ListOfPrimeFactors = new List<long>();\n        long Number = 600851475143;\n\n        // Check for smallest prime factor\n        for (long DividingNumber = 2; DividingNumber * DividingNumber <= Number; DividingNumber++)\n        {\n            while (Number % DividingNumber == 0)\n            {\n                ListOfPrimeFactors.Add(DividingNumber);\n                Number /= DividingNumber;\n            }\n        }\n\n        // If Number is still greater than 1, then it is a prime number\n        if (Number > 1)\n        {\n            ListOfPrimeFactors.Add(Number);\n        }\n\n        // Output the result\n        Console.WriteLine("Prime Factors: ");\n        foreach (var factor in ListOfPrimeFactors)\n        {\n            Console.WriteLine(factor);\n        }\n    }\n}