// Writing data in big-endian format\nusing (var stream = new FileStream("big_endian_data.bin", FileMode.Create, FileAccess.Write))\nusing (var writer = new BigEndianBinaryWriter(stream))\n{\n    writer.Write((int)12345);\n    writer.Write((short)54321);\n}\n\n// Reading data in big-endian format\nusing (var stream = new FileStream("big_endian_data.bin", FileMode.Open, FileAccess.Read))\nusing (var reader = new BigEndianBinaryReader(stream))\n{\n    int intValue = reader.ReadInt32();\n    short shortValue = reader.ReadInt16();\n}