using System;\nusing System.Net;\nusing System.Net.Sockets;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new socket\n        Socket listener = new Socket(AddressFamily.InterNetworkV6, SocketType.Stream, ProtocolType.Tcp);\n\n        // Enable DualMode so that the socket can handle both IPv4 and IPv6\n        listener.DualMode = true;\n\n        // Bind the socket to the port\n        listener.Bind(new IPEndPoint(IPAddress.IPv6Any, 12345));\n\n        // Start listening for incoming connection requests\n        listener.Listen(100);\n\n        Console.WriteLine("Server is listening on port 12345 for both IPv4 and IPv6 clients.");\n\n        while (true)\n        {\n            // Accept an incoming connection\n            Socket handler = listener.Accept();\n\n            // Handle the connection (this is just an example, so we close it immediately)\n            Console.WriteLine("Accepted connection from: " + handler.RemoteEndPoint);\n            handler.Close();\n        }\n    }\n}