public static class EnumHelper\n{\n    public static int GetEnumMemberValue<TEnum>(TEnum enumMember) where TEnum : Enum\n    {\n        return Convert.ToInt32(enumMember);\n    }\n}\n\n// Usage\nint complexityValueToStore = EnumHelper.GetEnumMemberValue(Complexity.Normal);  // Should store 1\nint priorityValueToStore = EnumHelper.GetEnumMemberValue(Priority.Urgent);      // Should store 4