using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Web.UI;\n\npublic partial class YourAspxPage : Page\n{\n    protected async void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            string phpContent = await FetchPhpContentAsync("http://yourdomain.com/yourphpscript.php");\n            phpContentPlaceholder.InnerHtml = phpContent;\n        }\n    }\n\n    private async Task<string> FetchPhpContentAsync(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            HttpResponseMessage response = await client.GetAsync(url);\n            response.EnsureSuccessStatusCode();\n            return await response.Content.ReadAsStringAsync();\n        }\n    }\n}