protected void rptPhones_ItemDataBound(object sender, RepeaterItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        var dataItem = (YourDataType)e.Item.DataItem; // Replace 'YourDataType' with the actual type of your data items\n        bool isChecked = GetIsChecked(dataItem.IsDefault);\n\n        var radioButton = (HtmlInputRadioButton)e.Item.FindControl("rbDefaultPhone_" + e.Item.ItemIndex);\n        radioButton.Checked = isChecked;\n    }\n}\n\nprotected bool GetIsChecked(object isDefault)\n{\n    // Implement your logic to determine if the radio button should be checked\n    return Convert.ToBoolean(isDefault);\n}